import{_ as e,o as c,c as d,d as o}from"./app.91a380bb.js";const a={},p=o('<h1 id="\u9762\u8BD5\u5B98-\u5982\u4F55\u7406\u89E3-cdn-\u8BF4\u8BF4\u5B9E\u73B0\u539F\u7406" tabindex="-1"><a class="header-anchor" href="#\u9762\u8BD5\u5B98-\u5982\u4F55\u7406\u89E3-cdn-\u8BF4\u8BF4\u5B9E\u73B0\u539F\u7406" aria-hidden="true">#</a> \u9762\u8BD5\u5B98\uFF1A\u5982\u4F55\u7406\u89E3 CDN\uFF1F\u8BF4\u8BF4\u5B9E\u73B0\u539F\u7406\uFF1F</h1><p><img src="https://static.vue-js.com/437ae0f0-b86b-11eb-85f6-6fac77c0c9b3.png" alt=""></p><h2 id="\u4E00\u3001\u662F\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#\u4E00\u3001\u662F\u4EC0\u4E48" aria-hidden="true">#</a> \u4E00\u3001\u662F\u4EC0\u4E48</h2><p>CDN (\u5168\u79F0 Content Delivery Network)\uFF0C\u5373\u5185\u5BB9\u5206\u53D1\u7F51\u7EDC</p><p>\u6784\u5EFA\u5728\u73B0\u6709\u7F51\u7EDC\u57FA\u7840\u4E4B\u4E0A\u7684\u667A\u80FD\u865A\u62DF\u7F51\u7EDC\uFF0C\u4F9D\u9760\u90E8\u7F72\u5728\u5404\u5730\u7684\u8FB9\u7F18\u670D\u52A1\u5668\uFF0C\u901A\u8FC7\u4E2D\u5FC3\u5E73\u53F0\u7684\u8D1F\u8F7D\u5747\u8861\u3001\u5185\u5BB9\u5206\u53D1\u3001\u8C03\u5EA6\u7B49\u529F\u80FD\u6A21\u5757\uFF0C\u4F7F\u7528\u6237\u5C31\u8FD1\u83B7\u53D6\u6240\u9700\u5185\u5BB9\uFF0C\u964D\u4F4E\u7F51\u7EDC\u62E5\u585E\uFF0C\u63D0\u9AD8\u7528\u6237\u8BBF\u95EE\u54CD\u5E94\u901F\u5EA6\u548C\u547D\u4E2D\u7387\u3002<code>CDN</code> \u7684\u5173\u952E\u6280\u672F\u4E3B\u8981\u6709\u5185\u5BB9\u5B58\u50A8\u548C\u5206\u53D1\u6280\u672F</p><p>\u7B80\u5355\u6765\u8BB2\uFF0C<code>CDN</code>\u5C31\u662F\u6839\u636E\u7528\u6237\u4F4D\u7F6E\u5206\u914D\u6700\u8FD1\u7684\u8D44\u6E90</p><p>\u4E8E\u662F\uFF0C\u7528\u6237\u5728\u4E0A\u7F51\u7684\u65F6\u5019\u4E0D\u7528\u76F4\u63A5\u8BBF\u95EE\u6E90\u7AD9\uFF0C\u800C\u662F\u8BBF\u95EE\u79BB\u4ED6\u201C\u6700\u8FD1\u7684\u201D\u4E00\u4E2A CDN \u8282\u70B9\uFF0C\u672F\u8BED\u53EB<strong>\u8FB9\u7F18\u8282\u70B9</strong>\uFF0C\u5176\u5B9E\u5C31\u662F\u7F13\u5B58\u4E86\u6E90\u7AD9\u5185\u5BB9\u7684\u4EE3\u7406\u670D\u52A1\u5668\u3002\u5982\u4E0B\u56FE\uFF1A</p><p><img src="https://static.vue-js.com/4f0289f0-b86b-11eb-85f6-6fac77c0c9b3.png" alt=""></p><h2 id="\u4E8C\u3001\u539F\u7406\u5206\u6790" tabindex="-1"><a class="header-anchor" href="#\u4E8C\u3001\u539F\u7406\u5206\u6790" aria-hidden="true">#</a> \u4E8C\u3001\u539F\u7406\u5206\u6790</h2><p>\u5728\u6CA1\u6709\u5E94\u7528<code>CDN</code>\u65F6\uFF0C\u6211\u4EEC\u4F7F\u7528\u57DF\u540D\u8BBF\u95EE\u67D0\u4E00\u4E2A\u7AD9\u70B9\u65F6\u7684\u8DEF\u5F84\u4E3A</p><blockquote><p>\u7528\u6237\u63D0\u4EA4\u57DF\u540D \u2192 \u6D4F\u89C8\u5668\u5BF9\u57DF\u540D\u8FDB\u884C\u89E3\u91CA \u2192<code>DNS</code> \u89E3\u6790\u5F97\u5230\u76EE\u7684\u4E3B\u673A\u7684 IP \u5730\u5740 \u2192 \u6839\u636E IP \u5730\u5740\u8BBF\u95EE\u53D1\u51FA\u8BF7\u6C42 \u2192 \u5F97\u5230\u8BF7\u6C42\u6570\u636E\u5E76\u56DE\u590D</p></blockquote><p>\u5E94\u7528<code>CDN</code>\u540E\uFF0C<code>DNS</code> \u8FD4\u56DE\u7684\u4E0D\u518D\u662F <code>IP</code> \u5730\u5740\uFF0C\u800C\u662F\u4E00\u4E2A<code>CNAME</code>(Canonical Name ) \u522B\u540D\u8BB0\u5F55\uFF0C\u6307\u5411<code>CDN</code>\u7684\u5168\u5C40\u8D1F\u8F7D\u5747\u8861</p><p><code>CNAME</code>\u5B9E\u9645\u4E0A\u5728\u57DF\u540D\u89E3\u6790\u7684\u8FC7\u7A0B\u4E2D\u627F\u62C5\u4E86\u4E2D\u95F4\u4EBA\uFF08\u6216\u8005\u8BF4\u4EE3\u7406\uFF09\u7684\u89D2\u8272\uFF0C\u8FD9\u662F<code>CDN</code>\u5B9E\u73B0\u7684\u5173\u952E</p><h4 id="\u8D1F\u8F7D\u5747\u8861\u7CFB\u7EDF" tabindex="-1"><a class="header-anchor" href="#\u8D1F\u8F7D\u5747\u8861\u7CFB\u7EDF" aria-hidden="true">#</a> \u8D1F\u8F7D\u5747\u8861\u7CFB\u7EDF</h4><p>\u7531\u4E8E\u6CA1\u6709\u8FD4\u56DE<code>IP</code>\u5730\u5740\uFF0C\u4E8E\u662F\u672C\u5730<code>DNS</code>\u4F1A\u5411\u8D1F\u8F7D\u5747\u8861\u7CFB\u7EDF\u518D\u53D1\u9001\u8BF7\u6C42 \uFF0C\u5219\u8FDB\u5165\u5230<code>CDN</code>\u7684\u5168\u5C40\u8D1F\u8F7D\u5747\u8861\u7CFB\u7EDF\u8FDB\u884C\u667A\u80FD\u8C03\u5EA6\uFF1A</p><ul><li><p>\u770B\u7528\u6237\u7684 IP \u5730\u5740\uFF0C\u67E5\u8868\u5F97\u77E5\u5730\u7406\u4F4D\u7F6E\uFF0C\u627E\u76F8\u5BF9\u6700\u8FD1\u7684\u8FB9\u7F18\u8282\u70B9</p></li><li><p>\u770B\u7528\u6237\u6240\u5728\u7684\u8FD0\u8425\u5546\u7F51\u7EDC\uFF0C\u627E\u76F8\u540C\u7F51\u7EDC\u7684\u8FB9\u7F18\u8282\u70B9</p></li><li><p>\u68C0\u67E5\u8FB9\u7F18\u8282\u70B9\u7684\u8D1F\u8F7D\u60C5\u51B5\uFF0C\u627E\u8D1F\u8F7D\u8F83\u8F7B\u7684\u8282\u70B9</p></li><li><p>\u5176\u4ED6\uFF0C\u6BD4\u5982\u8282\u70B9\u7684\u201C\u5065\u5EB7\u72B6\u51B5\u201D\u3001\u670D\u52A1\u80FD\u529B\u3001\u5E26\u5BBD\u3001\u54CD\u5E94\u65F6\u95F4\u7B49</p></li></ul><p>\u7ED3\u5408\u4E0A\u9762\u7684\u56E0\u7D20\uFF0C\u5F97\u5230\u6700\u5408\u9002\u7684\u8FB9\u7F18\u8282\u70B9\uFF0C\u7136\u540E\u628A\u8FD9\u4E2A\u8282\u70B9\u8FD4\u56DE\u7ED9\u7528\u6237\uFF0C\u7528\u6237\u5C31\u80FD\u591F\u5C31\u8FD1\u8BBF\u95EE<code>CDN</code>\u7684\u7F13\u5B58\u4EE3\u7406</p><p>\u6574\u4F53\u6D41\u7A0B\u5982\u4E0B\u56FE\uFF1A</p><p><img src="https://static.vue-js.com/588d7890-b86b-11eb-85f6-6fac77c0c9b3.png" alt=""></p><h4 id="\u7F13\u5B58\u4EE3\u7406" tabindex="-1"><a class="header-anchor" href="#\u7F13\u5B58\u4EE3\u7406" aria-hidden="true">#</a> \u7F13\u5B58\u4EE3\u7406</h4><p>\u7F13\u5B58\u7CFB\u7EDF\u662F <code>CDN </code>\u7684\u53E6\u4E00\u4E2A\u5173\u952E\u7EC4\u6210\u90E8\u5206\uFF0C\u7F13\u5B58\u7CFB\u7EDF\u4F1A\u6709\u9009\u62E9\u5730\u7F13\u5B58\u90A3\u4E9B\u6700\u5E38\u7528\u7684\u90A3\u4E9B\u8D44\u6E90</p><p>\u5176\u4E2D\u6709\u4E24\u4E2A\u8861\u91CF<code>CDN</code>\u670D\u52A1\u8D28\u91CF\u7684\u6307\u6807\uFF1A</p><ul><li>\u547D\u4E2D\u7387\uFF1A\u7528\u6237\u8BBF\u95EE\u7684\u8D44\u6E90\u6070\u597D\u5728\u7F13\u5B58\u7CFB\u7EDF\u91CC\uFF0C\u53EF\u4EE5\u76F4\u63A5\u8FD4\u56DE\u7ED9\u7528\u6237\uFF0C\u547D\u4E2D\u6B21\u6570\u4E0E\u6240\u6709\u8BBF\u95EE\u6B21\u6570\u4E4B\u6BD4</li><li>\u56DE\u6E90\u7387\uFF1A\u7F13\u5B58\u91CC\u6CA1\u6709\uFF0C\u5FC5\u987B\u7528\u4EE3\u7406\u7684\u65B9\u5F0F\u56DE\u6E90\u7AD9\u53D6\uFF0C\u56DE\u6E90\u6B21\u6570\u4E0E\u6240\u6709\u8BBF\u95EE\u6B21\u6570\u4E4B\u6BD4</li></ul><p>\u7F13\u5B58\u7CFB\u7EDF\u4E5F\u53EF\u4EE5\u5212\u5206\u51FA\u5C42\u6B21\uFF0C\u5206\u6210\u4E00\u7EA7\u7F13\u5B58\u8282\u70B9\u548C\u4E8C\u7EA7\u7F13\u5B58\u8282\u70B9\u3002\u4E00\u7EA7\u7F13\u5B58\u914D\u7F6E\u9AD8\u4E00\u4E9B\uFF0C\u76F4\u8FDE\u6E90\u7AD9\uFF0C\u4E8C\u7EA7\u7F13\u5B58\u914D\u7F6E\u4F4E\u4E00\u4E9B\uFF0C\u76F4\u8FDE\u7528\u6237</p><p>\u56DE\u6E90\u7684\u65F6\u5019\u4E8C\u7EA7\u7F13\u5B58\u53EA\u627E\u4E00\u7EA7\u7F13\u5B58\uFF0C\u4E00\u7EA7\u7F13\u5B58\u6CA1\u6709\u624D\u56DE\u6E90\u7AD9\uFF0C\u53EF\u4EE5\u6709\u6548\u5730\u51CF\u5C11\u771F\u6B63\u7684\u56DE\u6E90</p><p>\u73B0\u5728\u7684\u5546\u4E1A <code>CDN</code>\u547D\u4E2D\u7387\u90FD\u5728 90% \u4EE5\u4E0A\uFF0C\u76F8\u5F53\u4E8E\u628A\u6E90\u7AD9\u7684\u670D\u52A1\u80FD\u529B\u653E\u5927\u4E86 10 \u500D\u4EE5\u4E0A</p><h2 id="\u4E09\u3001\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u4E09\u3001\u603B\u7ED3" aria-hidden="true">#</a> \u4E09\u3001\u603B\u7ED3</h2><p><code>CDN</code> \u76EE\u7684\u662F\u4E3A\u4E86\u6539\u5584\u4E92\u8054\u7F51\u7684\u670D\u52A1\u8D28\u91CF\uFF0C\u901A\u4FD7\u4E00\u70B9\u8BF4\u5176\u5B9E\u5C31\u662F\u63D0\u9AD8\u8BBF\u95EE\u901F\u5EA6</p><p><code>CDN</code> \u6784\u5EFA\u4E86\u5168\u56FD\u3001\u5168\u7403\u7EA7\u522B\u7684\u4E13\u7F51\uFF0C\u8BA9\u7528\u6237\u5C31\u8FD1\u8BBF\u95EE\u4E13\u7F51\u91CC\u7684\u8FB9\u7F18\u8282\u70B9\uFF0C\u964D\u4F4E\u4E86\u4F20\u8F93\u5EF6\u8FDF\uFF0C\u5B9E\u73B0\u4E86\u7F51\u7AD9\u52A0\u901F</p><p>\u901A\u8FC7<code>CDN</code>\u7684\u8D1F\u8F7D\u5747\u8861\u7CFB\u7EDF\uFF0C\u667A\u80FD\u8C03\u5EA6\u8FB9\u7F18\u8282\u70B9\u63D0\u4F9B\u670D\u52A1\uFF0C\u76F8\u5F53\u4E8E<code>CDN</code>\u670D\u52A1\u7684\u5927\u8111\uFF0C\u800C\u7F13\u5B58\u7CFB\u7EDF\u76F8\u5F53\u4E8E<code>CDN</code>\u7684\u5FC3\u810F\uFF0C\u7F13\u5B58\u547D\u4E2D\u76F4\u63A5\u8FD4\u56DE\u7ED9\u7528\u6237\uFF0C\u5426\u5219\u56DE\u6E90</p>',30),t=[p];function i(r,h){return c(),d("div",null,t)}const s=e(a,[["render",i],["__file","CDN.html.vue"]]);export{s as default};
