import{_ as e,o as a,c as d,d as c}from"./app.91a380bb.js";const i={},h=c('<h1 id="\u9762\u8BD5\u5B98-\u5982\u4F55\u7406\u89E3-osi-\u4E03\u5C42\u6A21\u578B" tabindex="-1"><a class="header-anchor" href="#\u9762\u8BD5\u5B98-\u5982\u4F55\u7406\u89E3-osi-\u4E03\u5C42\u6A21\u578B" aria-hidden="true">#</a> \u9762\u8BD5\u5B98\uFF1A\u5982\u4F55\u7406\u89E3 OSI \u4E03\u5C42\u6A21\u578B?</h1><p><img src="https://static.vue-js.com/e2e1b910-b61e-11eb-ab90-d9ae814b240d.png" alt=""></p><h2 id="\u4E00\u3001\u662F\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#\u4E00\u3001\u662F\u4EC0\u4E48" aria-hidden="true">#</a> \u4E00\u3001\u662F\u4EC0\u4E48</h2><p>OSI \uFF08Open System Interconnect\uFF09\u6A21\u578B\u5168\u79F0\u4E3A\u5F00\u653E\u5F0F\u901A\u4FE1\u7CFB\u7EDF\u4E92\u8FDE\u53C2\u8003\u6A21\u578B\uFF0C\u662F\u56FD\u9645\u6807\u51C6\u5316\u7EC4\u7EC7 ( ISO ) \u63D0\u51FA\u7684\u4E00\u4E2A\u8BD5\u56FE\u4F7F\u5404\u79CD\u8BA1\u7B97\u673A\u5728\u4E16\u754C\u8303\u56F4\u5185\u4E92\u8FDE\u4E3A\u7F51\u7EDC\u7684\u6807\u51C6\u6846\u67B6</p><p><code>OSI </code>\u5C06\u8BA1\u7B97\u673A\u7F51\u7EDC\u4F53\u7CFB\u7ED3\u6784\u5212\u5206\u4E3A\u4E03\u5C42\uFF0C\u6BCF\u4E00\u5C42\u5B9E\u73B0\u5404\u81EA\u7684\u529F\u80FD\u548C\u534F\u8BAE\uFF0C\u5E76\u5B8C\u6210\u4E0E\u76F8\u90BB\u5C42\u7684\u63A5\u53E3\u901A\u4FE1\u3002\u5373\u6BCF\u4E00\u5C42\u626E\u6F14\u56FA\u5B9A\u7684\u89D2\u8272\uFF0C\u4E92\u4E0D\u6253\u6270</p><h2 id="\u4E8C\u3001\u5212\u5206" tabindex="-1"><a class="header-anchor" href="#\u4E8C\u3001\u5212\u5206" aria-hidden="true">#</a> \u4E8C\u3001\u5212\u5206</h2><p><code>OSI</code>\u4E3B\u8981\u5212\u5206\u4E86\u4E03\u5C42\uFF0C\u5982\u4E0B\u56FE\u6240\u793A\uFF1A</p><p><img src="https://static.vue-js.com/eb1b2170-b61e-11eb-ab90-d9ae814b240d.png" alt=""></p><h3 id="\u5E94\u7528\u5C42" tabindex="-1"><a class="header-anchor" href="#\u5E94\u7528\u5C42" aria-hidden="true">#</a> \u5E94\u7528\u5C42</h3><p>\u5E94\u7528\u5C42\u4F4D\u4E8E OSI \u53C2\u8003\u6A21\u578B\u7684\u7B2C\u4E03\u5C42\uFF0C\u5176\u4F5C\u7528\u662F\u901A\u8FC7\u5E94\u7528\u7A0B\u5E8F\u95F4\u7684\u4EA4\u4E92\u6765\u5B8C\u6210\u7279\u5B9A\u7684\u7F51\u7EDC\u5E94\u7528</p><p>\u8BE5\u5C42\u534F\u8BAE\u5B9A\u4E49\u4E86\u5E94\u7528\u8FDB\u7A0B\u4E4B\u95F4\u7684\u4EA4\u4E92\u89C4\u5219\uFF0C\u901A\u8FC7\u4E0D\u540C\u7684\u5E94\u7528\u5C42\u534F\u8BAE\u4E3A\u4E0D\u540C\u7684\u7F51\u7EDC\u5E94\u7528\u63D0\u4F9B\u670D\u52A1\u3002\u4F8B\u5982\u57DF\u540D\u7CFB\u7EDF <code>DNS</code>\uFF0C\u652F\u6301\u4E07\u7EF4\u7F51\u5E94\u7528\u7684 <code>HTTP</code> \u534F\u8BAE\uFF0C\u7535\u5B50\u90AE\u4EF6\u7CFB\u7EDF\u91C7\u7528\u7684 <code>SMTP </code>\u534F\u8BAE\u7B49</p><p>\u5728\u5E94\u7528\u5C42\u4EA4\u4E92\u7684\u6570\u636E\u5355\u5143\u6211\u4EEC\u79F0\u4E4B\u4E3A\u62A5\u6587</p><h3 id="\u8868\u793A\u5C42" tabindex="-1"><a class="header-anchor" href="#\u8868\u793A\u5C42" aria-hidden="true">#</a> \u8868\u793A\u5C42</h3><p>\u8868\u793A\u5C42\u7684\u4F5C\u7528\u662F\u4F7F\u901A\u4FE1\u7684\u5E94\u7528\u7A0B\u5E8F\u80FD\u591F\u89E3\u91CA\u4EA4\u6362\u6570\u636E\u7684\u542B\u4E49\uFF0C\u5176\u4F4D\u4E8E <code>OSI </code>\u53C2\u8003\u6A21\u578B\u7684\u7B2C\u516D\u5C42\uFF0C\u5411\u4E0A\u4E3A\u5E94\u7528\u5C42\u63D0\u4F9B\u670D\u52A1\uFF0C\u5411\u4E0B\u63A5\u6536\u6765\u81EA\u4F1A\u8BDD\u5C42\u7684\u670D\u52A1</p><p>\u8BE5\u5C42\u63D0\u4F9B\u7684\u670D\u52A1\u4E3B\u8981\u5305\u62EC\u6570\u636E\u538B\u7F29\uFF0C\u6570\u636E\u52A0\u5BC6\u4EE5\u53CA\u6570\u636E\u63CF\u8FF0\uFF0C\u4F7F\u5E94\u7528\u7A0B\u5E8F\u4E0D\u5FC5\u62C5\u5FC3\u5728\u5404\u53F0\u8BA1\u7B97\u673A\u4E2D\u8868\u793A\u548C\u5B58\u50A8\u7684\u5185\u90E8\u683C\u5F0F\u5DEE\u5F02</p><h3 id="\u4F1A\u8BDD\u5C42" tabindex="-1"><a class="header-anchor" href="#\u4F1A\u8BDD\u5C42" aria-hidden="true">#</a> \u4F1A\u8BDD\u5C42</h3><p>\u4F1A\u8BDD\u5C42\u5C31\u662F\u8D1F\u8D23\u5EFA\u7ACB\u3001\u7BA1\u7406\u548C\u7EC8\u6B62\u8868\u793A\u5C42\u5B9E\u4F53\u4E4B\u95F4\u7684\u901A\u4FE1\u4F1A\u8BDD</p><p>\u8BE5\u5C42\u63D0\u4F9B\u4E86\u6570\u636E\u4EA4\u6362\u7684\u5B9A\u754C\u548C\u540C\u6B65\u529F\u80FD\uFF0C\u5305\u62EC\u4E86\u5EFA\u7ACB\u68C0\u67E5\u70B9\u548C\u6062\u590D\u65B9\u6848\u7684\u65B9\u6CD5</p><h3 id="\u4F20\u8F93\u5C42" tabindex="-1"><a class="header-anchor" href="#\u4F20\u8F93\u5C42" aria-hidden="true">#</a> \u4F20\u8F93\u5C42</h3><p>\u4F20\u8F93\u5C42\u7684\u4E3B\u8981\u4EFB\u52A1\u662F\u4E3A\u4E24\u53F0\u4E3B\u673A\u8FDB\u7A0B\u4E4B\u95F4\u7684\u901A\u4FE1\u63D0\u4F9B\u670D\u52A1\uFF0C\u5904\u7406\u6570\u636E\u5305\u9519\u8BEF\u3001\u6570\u636E\u5305\u6B21\u5E8F\uFF0C\u4EE5\u53CA\u5176\u4ED6\u4E00\u4E9B\u5173\u952E\u4F20\u8F93\u95EE\u9898</p><p>\u4F20\u8F93\u5C42\u5411\u9AD8\u5C42\u5C4F\u853D\u4E86\u4E0B\u5C42\u6570\u636E\u901A\u4FE1\u7684\u7EC6\u8282\u3002\u56E0\u6B64\uFF0C\u5B83\u662F\u8BA1\u7B97\u673A\u901A\u4FE1\u4F53\u7CFB\u7ED3\u6784\u4E2D\u5173\u952E\u7684\u4E00\u5C42</p><p>\u5176\u4E2D\uFF0C\u4E3B\u8981\u7684\u4F20\u8F93\u5C42\u534F\u8BAE\u662F<code>TCP</code>\u548C<code>UDP</code></p><h3 id="\u7F51\u7EDC\u5C42" tabindex="-1"><a class="header-anchor" href="#\u7F51\u7EDC\u5C42" aria-hidden="true">#</a> \u7F51\u7EDC\u5C42</h3><p>\u4E24\u53F0\u8BA1\u7B97\u673A\u4E4B\u95F4\u4F20\u9001\u6570\u636E\u65F6\u5176\u901A\u4FE1\u94FE\u8DEF\u5F80\u5F80\u4E0D\u6B62\u4E00\u6761\uFF0C\u6240\u4F20\u8F93\u7684\u4FE1\u606F\u751A\u81F3\u53EF\u80FD\u7ECF\u8FC7\u5F88\u591A\u901A\u4FE1\u5B50\u7F51</p><p>\u7F51\u7EDC\u5C42\u7684\u4E3B\u8981\u4EFB\u52A1\u5C31\u662F\u9009\u62E9\u5408\u9002\u7684\u7F51\u95F4\u8DEF\u7531\u548C\u4EA4\u6362\u8282\u70B9\uFF0C\u786E\u4FDD\u6570\u636E\u6309\u65F6\u6210\u529F\u4F20\u9001</p><p>\u5728\u53D1\u9001\u6570\u636E\u65F6\uFF0C\u7F51\u7EDC\u5C42\u628A\u4F20\u8F93\u5C42\u4EA7\u751F\u7684\u62A5\u6587\u6216\u7528\u6237\u6570\u636E\u62A5\u5C01\u88C5\u6210\u5206\u7EC4\u548C\u5305\uFF0C\u5411\u4E0B\u4F20\u8F93\u5230\u6570\u636E\u94FE\u8DEF\u5C42</p><p>\u5728\u7F51\u7EDC\u5C42\u4F7F\u7528\u7684\u534F\u8BAE\u662F\u65E0\u8FDE\u63A5\u7684\u7F51\u9645\u534F\u8BAE\uFF08Internet Protocol\uFF09\u548C\u8BB8\u591A\u8DEF\u7531\u534F\u8BAE\uFF0C\u56E0\u6B64\u6211\u4EEC\u901A\u5E38\u628A\u8BE5\u5C42\u7B80\u5355\u5730\u79F0\u4E3A IP \u5C42</p><h3 id="\u6570\u636E\u94FE\u8DEF\u5C42" tabindex="-1"><a class="header-anchor" href="#\u6570\u636E\u94FE\u8DEF\u5C42" aria-hidden="true">#</a> \u6570\u636E\u94FE\u8DEF\u5C42</h3><p>\u6570\u636E\u94FE\u8DEF\u5C42\u901A\u5E38\u4E5F\u53EB\u505A\u94FE\u8DEF\u5C42\uFF0C\u5728\u7269\u7406\u5C42\u548C\u7F51\u7EDC\u5C42\u4E4B\u95F4\u3002\u4E24\u53F0\u4E3B\u673A\u4E4B\u95F4\u7684\u6570\u636E\u4F20\u8F93\uFF0C\u603B\u662F\u5728\u4E00\u6BB5\u4E00\u6BB5\u7684\u94FE\u8DEF\u4E0A\u4F20\u9001\u7684\uFF0C\u8FD9\u5C31\u9700\u8981\u4F7F\u7528\u4E13\u95E8\u7684\u94FE\u8DEF\u5C42\u534F\u8BAE</p><p>\u5728\u4E24\u4E2A\u76F8\u90BB\u8282\u70B9\u4E4B\u95F4\u4F20\u9001\u6570\u636E\u65F6\uFF0C\u6570\u636E\u94FE\u8DEF\u5C42\u5C06\u7F51\u7EDC\u5C42\u4EA4\u4E0B\u6765\u7684 <code>IP </code>\u6570\u636E\u62A5\u7EC4\u88C5\u6210\u5E27\uFF0C\u5728\u4E24\u4E2A\u76F8\u90BB\u8282\u70B9\u95F4\u7684\u94FE\u8DEF\u4E0A\u4F20\u9001\u5E27</p><p>\u6BCF\u4E00\u5E27\u7684\u6570\u636E\u53EF\u4EE5\u5206\u6210\uFF1A\u62A5\u5934<code>head</code>\u548C\u6570\u636E<code>data</code>\u4E24\u90E8\u5206:</p><ul><li>head \u6807\u660E\u6570\u636E\u53D1\u9001\u8005\u3001\u63A5\u53D7\u8005\u3001\u6570\u636E\u7C7B\u578B\uFF0C\u5982 MAC \u5730\u5740</li><li>data \u5B58\u50A8\u4E86\u8BA1\u7B97\u673A\u4E4B\u95F4\u4EA4\u4E92\u7684\u6570\u636E</li></ul><p>\u901A\u8FC7\u63A7\u5236\u4FE1\u606F\u6211\u4EEC\u53EF\u4EE5\u77E5\u9053\u4E00\u4E2A\u5E27\u7684\u8D77\u6B62\u6BD4\u7279\u4F4D\u7F6E\uFF0C\u6B64\u5916\uFF0C\u4E5F\u80FD\u4F7F\u63A5\u6536\u7AEF\u68C0\u6D4B\u51FA\u6240\u6536\u5230\u7684\u5E27\u6709\u65E0\u5DEE\u9519\uFF0C\u5982\u679C\u53D1\u73B0\u5DEE\u9519\uFF0C\u6570\u636E\u94FE\u8DEF\u5C42\u80FD\u591F\u7B80\u5355\u7684\u4E22\u5F03\u6389\u8FD9\u4E2A\u5E27\uFF0C\u4EE5\u907F\u514D\u7EE7\u7EED\u5360\u7528\u7F51\u7EDC\u8D44\u6E90</p><h3 id="\u7269\u7406\u5C42" tabindex="-1"><a class="header-anchor" href="#\u7269\u7406\u5C42" aria-hidden="true">#</a> \u7269\u7406\u5C42</h3><p>\u4F5C\u4E3A<code> OSI</code> \u53C2\u8003\u6A21\u578B\u4E2D\u6700\u4F4E\u7684\u4E00\u5C42\uFF0C\u7269\u7406\u5C42\u7684\u4F5C\u7528\u662F\u5B9E\u73B0\u8BA1\u7B97\u673A\u8282\u70B9\u4E4B\u95F4\u6BD4\u7279\u6D41\u7684\u900F\u660E\u4F20\u9001</p><p>\u8BE5\u5C42\u7684\u4E3B\u8981\u4EFB\u52A1\u662F\u786E\u5B9A\u4E0E\u4F20\u8F93\u5A92\u4F53\u7684\u63A5\u53E3\u7684\u4E00\u4E9B\u7279\u6027\uFF08\u673A\u68B0\u7279\u6027\u3001\u7535\u6C14\u7279\u6027\u3001\u529F\u80FD\u7279\u6027\uFF0C\u8FC7\u7A0B\u7279\u6027\uFF09</p><p>\u8BE5\u5C42\u4E3B\u8981\u662F\u548C\u786C\u4EF6\u6709\u5173\uFF0C\u4E0E\u8F6F\u4EF6\u5173\u7CFB\u4E0D\u5927</p><h2 id="\u4E09\u3001\u4F20\u8F93\u8FC7\u7A0B" tabindex="-1"><a class="header-anchor" href="#\u4E09\u3001\u4F20\u8F93\u8FC7\u7A0B" aria-hidden="true">#</a> \u4E09\u3001\u4F20\u8F93\u8FC7\u7A0B</h2><p>\u6570\u636E\u5728\u5404\u5C42\u4E4B\u95F4\u7684\u4F20\u8F93\u5982\u4E0B\u56FE\u6240\u793A\uFF1A</p><p><img src="https://static.vue-js.com/f3a89d40-b61e-11eb-85f6-6fac77c0c9b3.png" alt=""></p><ul><li>\u5E94\u7528\u5C42\u62A5\u6587\u88AB\u4F20\u9001\u5230\u8FD0\u8F93\u5C42</li><li>\u5728\u6700\u7B80\u5355\u7684\u60C5\u51B5\u4E0B\uFF0C\u8FD0\u8F93\u5C42\u6536\u53D6\u5230\u62A5\u6587\u5E76\u9644\u4E0A\u9644\u52A0\u4FE1\u606F\uFF0C\u8BE5\u9996\u90E8\u5C06\u88AB\u63A5\u6536\u7AEF\u7684\u8FD0\u8F93\u5C42\u4F7F\u7528</li><li>\u5E94\u7528\u5C42\u62A5\u6587\u548C\u8FD0\u8F93\u5C42\u9996\u90E8\u4FE1\u606F\u4E00\u9053\u6784\u6210\u4E86\u8FD0\u8F93\u5C42\u62A5\u6587\u6BB5\u3002\u9644\u52A0\u7684\u4FE1\u606F\u53EF\u80FD\u5305\u62EC\uFF1A\u5141\u8BB8\u63A5\u6536\u7AEF\u8FD0\u8F93\u5C42\u5411\u4E0A\u5411\u9002\u5F53\u7684\u5E94\u7528\u7A0B\u5E8F\u4EA4\u4ED8\u62A5\u6587\u7684\u4FE1\u606F\u4EE5\u53CA\u5DEE\u9519\u68C0\u6D4B\u4F4D\u4FE1\u606F\u3002\u8BE5\u4FE1\u606F\u8BA9\u63A5\u6536\u7AEF\u80FD\u591F\u5224\u65AD\u62A5\u6587\u4E2D\u7684\u6BD4\u7279\u662F\u5426\u5728\u9014\u4E2D\u5DF2\u88AB\u6539\u53D8</li><li>\u8FD0\u8F93\u5C42\u5219\u5411\u7F51\u7EDC\u5C42\u4F20\u9012\u8BE5\u62A5\u6587\u6BB5\uFF0C\u7F51\u7EDC\u5C42\u589E\u52A0\u4E86\u5982\u6E90\u548C\u76EE\u7684\u7AEF\u7CFB\u7EDF\u5730\u5740\u7B49\u7F51\u7EDC\u5C42\u9996\u90E8\u4FE1\u606F\uFF0C\u751F\u6210\u4E86\u7F51\u7EDC\u5C42\u6570\u636E\u62A5</li><li>\u7F51\u7EDC\u5C42\u6570\u636E\u62A5\u63A5\u4E0B\u6765\u88AB\u4F20\u9012\u7ED9\u94FE\u8DEF\u5C42\uFF0C\u5728\u6570\u636E\u94FE\u8DEF\u5C42\u6570\u636E\u5305\u6DFB\u52A0\u53D1\u9001\u7AEF MAC \u5730\u5740\u548C\u63A5\u6536\u7AEF MAC \u5730\u5740\u540E\u88AB\u5C01\u88C5\u6210\u6570\u636E\u5E27</li><li>\u5728\u7269\u7406\u5C42\u6570\u636E\u5E27\u88AB\u5C01\u88C5\u6210\u6BD4\u7279\u6D41\uFF0C\u4E4B\u540E\u901A\u8FC7\u4F20\u8F93\u4ECB\u8D28\u4F20\u9001\u5230\u5BF9\u7AEF</li><li>\u5BF9\u7AEF\u518D\u4E00\u6B65\u6B65\u89E3\u5F00\u5C01\u88C5\uFF0C\u83B7\u53D6\u5230\u4F20\u9001\u7684\u6570\u636E</li></ul>',41),r=[h];function p(t,o){return a(),d("div",null,r)}const s=e(i,[["render",p],["__file","OSI.html.vue"]]);export{s as default};
